    max-width: 800px;
    margin: 40px auto;
    padding: 40px;
    background-color: var(--card-bg);
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
}

.hidden {
    display: none;
}

/* Documentation Styling */
