document.addEventListener('DOMContentLoaded', () => {
    console.log("Gemini Playground: DOM Content Loaded");

    // Elements
    const body = document.body;
    const themeToggle = document.getElementById('theme-toggle');
    const viewAppBtn = document.getElementById('view-app');
    const viewDocsBtn = document.getElementById('view-docs');
    const appContent = document.getElementById('app-content');
    const docsContent = document.getElementById('docs-content');
    const tabBtns = document.querySelectorAll('.tab-btn');
    const docPanes = document.querySelectorAll('.doc-pane');

    // Thought Stream Elements
    const thoughtInput = document.getElementById('thought-input');
    const addThoughtBtn = document.getElementById('add-thought-btn');
    const thoughtsList = document.getElementById('thoughts-list');

    // Debugging: Check if elements exist
    console.log({
        themeToggle, viewAppBtn, viewDocsBtn, appContent, docsContent,
        thoughtInput, addThoughtBtn, thoughtsList
    });

    if (!addThoughtBtn) console.error("Critical: 'Log to Stream' button not found!");

    // State
    let thoughts = [];
    try {
        const saved = localStorage.getItem('thoughts');
        if (saved) thoughts = JSON.parse(saved);
    } catch (e) {
        console.error("Error reading localStorage:", e);
    }

    // Theme Management
    const currentTheme = localStorage.getItem('theme');
    if (currentTheme) {
        body.classList.add(currentTheme);
        updateThemeUI();
    }

