        if (!themeToggle) return;
        const isDark = body.classList.contains('dark-mode');
        themeToggle.textContent = isDark ? '☀️ Light Mode' : '🌙 Dark Mode';
    }

    // Navigation Management
    if (viewAppBtn && viewDocsBtn) {
        viewAppBtn.addEventListener('click', () => {
            console.log("Nav: View App clicked");
            viewAppBtn.classList.add('active');
            viewDocsBtn.classList.remove('active');
            appContent.classList.remove('hidden');
            docsContent.classList.add('hidden');
        });

        viewDocsBtn.addEventListener('click', () => {
            console.log("Nav: View Docs clicked");
            viewDocsBtn.classList.add('active');
            viewAppBtn.classList.remove('active');
            docsContent.classList.remove('hidden');
            appContent.classList.add('hidden');
        });
    }

    // Documentation Tabs
    tabBtns.forEach(btn => {
        btn.addEventListener('click', () => {
            const targetId = btn.getAttribute('data-target');
            console.log("Tab clicked:", targetId);

            // Update button active state
            tabBtns.forEach(b => b.classList.remove('active'));
            btn.classList.add('active');

            // Show target pane
            docPanes.forEach(pane => {
                if (pane.id === targetId) {
                    pane.classList.remove('hidden');
                } else {
                    pane.classList.add('hidden');
                }
            });
        });
    });

    // Thought Stream Logic
    function renderThoughts() {
        if (!thoughtsList) return;
        thoughtsList.innerHTML = '';

        if (thoughts.length === 0) {
            thoughtsList.innerHTML = '<p style="text-align: center; opacity: 0.6;">No thoughts logged yet. Start the stream!</p>';
            return;
        }

        // Sort by newest first
        thoughts.slice().reverse().forEach((thought, index) => {
            const actualIndex = thoughts.length - 1 - index; 

            const card = document.createElement('div');
            card.className = 'thought-card';

            const date = new Date(thought.timestamp).toLocaleString();

            card.innerHTML = `
                <div class="thought-header">
                    <span class="timestamp">${date}</span>
                    <button class="delete-btn" onclick="deleteThought(${actualIndex})" title="Delete Thought">&times;</button>
                </div>
                <div class="thought-content">${escapeHtml(thought.text)}</div>
            `;

            thoughtsList.appendChild(card);
        });
    }

    function addThought() {
        console.log("Attempting to add thought...");
        if (!thoughtInput) return;

        const text = thoughtInput.value.trim();
        if (!text) {
            console.log("Input is empty");
            return;
        }

        const newThought = {
            text: text,
            timestamp: Date.now()
        };

        thoughts.push(newThought);
        try {
            localStorage.setItem('thoughts', JSON.stringify(thoughts));
        } catch (e) {
            console.error("Error saving to localStorage:", e);
        }

        thoughtInput.value = '';
        renderThoughts();
        console.log("Thought added:", newThought);
    }

    // Expose delete function to window scope
    window.deleteThought = function(index) {
        console.log("Deleting thought at index:", index);
        if(confirm('Are you sure you want to delete this thought?')) {
            thoughts.splice(index, 1);
            try {
                localStorage.setItem('thoughts', JSON.stringify(thoughts));
            } catch (e) {
                console.error("Error saving to localStorage:", e);
            }
            renderThoughts();
        }
    };

    function escapeHtml(text) {
        const div = document.createElement('div');
        div.textContent = text;
        return div.innerHTML.replace(/\n/g, '<br>');
    }

    if (addThoughtBtn) {
        addThoughtBtn.addEventListener('click', addThought);
    }

    if (thoughtInput) {
        thoughtInput.addEventListener('keydown', (e) => {
            if (e.ctrlKey && e.key === 'Enter') {
                addThought();
            }
        });
    }

    // Initial Render
    renderThoughts();
});
