args.content:
$shortcutPath = "$env:APPDATA\Microsoft\Windows\Start Menu\Programs\Gemini CLI.lnk"
$wsh = New-Object -ComObject WScript.Shell

if (Test-Path $shortcutPath) {
    $sc = $wsh.CreateShortcut($shortcutPath)
    Write-Host "Found shortcut at: $shortcutPath"
    Write-Host "Current Target: $($sc.TargetPath)"
    Write-Host "Current Working Directory: $($sc.WorkingDirectory)"

    # 1. Set the Working Directory
    $sc.WorkingDirectory = "C:\Users\Mischa\Documents\M-Claude"
    Write-Host "Updating Working Directory to: $($sc.WorkingDirectory)"

    # 2. Ensure the window stays open (wrap in cmd /k if not already)
    if ($sc.TargetPath -notmatch "cmd.exe") {
        $originalTarget = $sc.TargetPath
        $originalArgs = $sc.Arguments

        # Check if the target is an executable or script
        # Assuming it points to gemini.cmd or similar

        $sc.Arguments = "/k ""$originalTarget"" $originalArgs"
        $sc.TargetPath = "C:\Windows\System32\cmd.exe"
        Write-Host "Updated Target to keep window open."
    } else {
        Write-Host "Target already seems to be wrapped in cmd.exe, checking arguments..."
        if ($sc.Arguments -notmatch "/k") {
             $sc.Arguments = "/k " + $sc.Arguments
             Write-Host "Added /k to arguments."
        }
    }

    $sc.Save()
    Write-Host "Shortcut updated successfully!"
} else {
    Write-Host "Error: Shortcut not found at $shortcutPath"
    # Fallback search or prompt
}

